fn part1(input: String) -> i32 {
    let mut counter = 0;
    for x in 0..input.len() {
        if input[x..x + 1].to_string() == "(" {
            counter += 1;
        }
        if input[x..x + 1].to_string() == ")" {
            counter -= 1;
        }
    }
    counter
}

fn part2(input: String) -> i32 {
    let mut counter = 0;
    let mut position = 0;
    for x in 0..input.len() {
        let character = input[x..x + 1].to_string();
        if counter == -1 {
            break;
        }

        if character== "(" {
            counter += 1;
        }
        if character == ")" {
            counter -= 1;
        }
        position+=1;
    }
    position
}

/*
For example:

    (()) and ()() both result in floor 0.
    ((( and (()(()( both result in floor 3.
    ))((((( also results in floor 3.
    ()) and ))( both result in floor -1 (the first basement level).
    ))) and )())()) both result in floor -3.

 */
fn main() {
    let input = get_input();

    println!("{}", part1(get_input().to_string()));
    println!("{}", part2(get_input().to_string()));
}
